import './ExpemseItem.css';
import React, { useState } from "react";

import ExpenseDate from './ExpenseDate';
function ExpenseItem(props) { //making of components


    // let title = props.title;
    const [Newtitle, setNewTitle] = useState("1213");
    const [title, setTitle] = useState(props.title);//setTitle is function 

    function clickhandler() {
        setTitle('New Title...');

        // title = "new titile";
        // console.log(title);
        // alert("New Title");
    }

    return (
        <div className="expense-item">
            <ExpenseDate date={props.date} />
            <div className="expense-info">
                <h2>{title}</h2>
                <div className="expense-price">${props.amount}</div>
            </div>
            <input type="number" value = {Newtitle}></input>
            <button onClick={clickhandler}>Change Title</button>

        </div>
    );
}
//{ props.date.toISOString()}
export default ExpenseItem;
