import React, { useEffect, useState } from "react";
import Expenses from './Components/Expenses';
import NewExpense from './Components/NewExpense/NewExpense';
import axios from 'axios';

let DUMMY_EXPENSE = [ //making of arrays

];

function App() {

    const [expenses, setExpenses] = useState(DUMMY_EXPENSE);

    // const fetchApi = async () => {
    //     try{
    //         const res = await axios.get('http:// 192.168.61.140:8080/expenseitem')
    //         console.log(res.data);
    //     }
    //     catch(error){
    //         console.log(error.messasge);
    //     }
       
    // }

    // useEffect(() =>{
    //     fetchApi()
    // }, [])

    fetch('http://192.168.61.140:8080/expenseitem', {
        mode: 'no-cors'
    }).then(
        (response) => {
            response.json().then(
                (data) => {
                    console.log("response", data)
                    setExpenses(data);
                })
        })

    function addExpenseHandler(expense) {
        const updateExpense = [expense, ...expenses];
        setExpenses(updateExpense);
    }
    return (
        <div>
            <NewExpense onAddExpense={addExpenseHandler} />
            <Expenses item={expenses} />
        </div>


    );
}

export default App;
