import React, { useState } from "react";
import './app';

import ExpenseDate from './ExpenseDate';
function ExpenseItem(props) { //making of components


    // let title = props.title;
    const [Newtitle, setNewTitle] = useState("121");
    const [amount, setTitle] = useState(props.amount);//setTitle is function 

    function clickhandler() {
        setTitle('New Title...');

        // title = "new titile";
        // console.log(title);
        // alert("New Title");
    }

    function changeHandler (event){
        setNewTitle(event.target.value);
    }

    return (
        <div className="expense-item">
            <ExpenseDate date={props.date} />
            <div className="expense-info">
                <h2>{title}</h2>
                <div className="expense-price">${props.amount} {amount}</div>
            </div>
            <input type="number" value = {Newtitle}  onChange = {changeHandler}></input>
            <button onClick={clickhandler}>Change Title</button>

        </div>
    );
}
//{ props.date.toISOString()}
export default ExpenseItem;
